import{B as o}from"./BgSection.ffad9ed8.js";import{R as i}from"./Clientes.131558f2.js";import{V as r}from"./Valores.9c5f1748.js";import{H as n}from"./HeroText.d722fe02.js";import{a as s,j as e,d as t}from"./index.31b8c980.js";import"./Card.02310fd2.js";const d=[{id:"001",title:"SEGURYGUARDIA",content:"Seguryguardia limitada es una empresa prestadora de servicios de seguridad. Dedicada a evaluar, controlar y proteger bienes y activos de las diferentes empresas"},{id:"002",title:"\xBFQUIENES SOMOS?",content:"Somos una empresa prestadora de servicios de seguridad, con alta cobertura en la zona norte de nuestro pa\xEDs. Destacamos con gran sentido la responsabilidad y excelencia en servicio, nuestro equipo y encargado de operaciones cuentan con un amplio conocimientos en el \xE1rea. Nuestra empresa se desempe\xF1a dentro de los par\xE1metros sobre la legislaci\xF3n y normativa actual que regula el funcionamiento de las actividades en nuestros servicios. El personal se capacita en las siguientes materia: <ul><li>Ley N\xB03607 </li><li>Decreto Supremo N\xB093</li></ul>"},{id:"003",title:"MISI\xD3N",content:"A trav\xE9s de nuestras t\xE9cnicas modernas de administraci\xF3n; aconsejamos a las diferentes empresas, instituciones, condominios, entidades p\xFAblicas y privadas en general, a enfocar todos sus esfuerzos hacia el giro de la actividad que le son propios"},{id:"004",title:"\xBFQUI\xC9NES SOMOS?",content:"Somos la asociaci\xF3n Gremial de Due\xF1os de Camiones de Coquimbo y nuestra misi\xF3n es unificar a los peque\xF1os y medianos transportistas de la regi\xF3n de Coquimbo para poder ser m\xE1s competitivos en el mercado del transporte de carga, concretando trabajos seguros y pagos de sus facturas a tiempo, proporcionando una red de apoyo log\xEDstico para los transportistas y asesorarlos en su gesti\xF3n. De esa manera se benificia m\xE1s de 150 familias de la regi\xF3n, esto incluye a los due\xF1os de camiones y sus choferes, lo que permite que la econom\xEDa de la regi\xF3n avance. Adem\xE1s, Asiducam ayuda a que sus socios puedan renovar sus equipos y de esa manera ir a la vanguardia del transporte, algo que si no fuera por la uni\xF3n que logra la Asociaci\xF3n Gremial ser\xEDa casi imposible.<br />Actualmente Asiducam est\xE1 compueta por 78 socios, de los cuales 65 est\xE1n activos. <br />Desde el a\xF1o 2018 Asiducam es miembro activo de la Comunidad Log\xEDstica Portuar\xEDa Coquimbo (<strong>CLPC</strong>)"}],l={empresa:d},y=()=>s("section",{className:"bg-light animate__animated animate__fadeIn",children:[e(o,{title:"empresa",subtitle:"Quienes Somos?",image:""}),s(t,{className:"p-5 bg-body shadow",children:[e(n,{}),l.empresa.map(a=>s("div",{children:[e("h4",{className:"mt-5 fw-bold fs-2 mb-4",style:{color:"#347"},children:a.title}),e("p",{style:{color:"#333333"},dangerouslySetInnerHTML:{__html:a.content}}),e("hr",{})]},a.id))]}),e(r,{}),e(i,{})]});export{y as default};
